// Image sources.
//

function getPlausibleIconDefault() {
  return "";
}

function getPlausibleIconWhite() {
  return "";
}

function getPlausibleIconBlack() {
  return "";
}

function getExternalLinkIconWhite() {
  return "";
}

function getPlausibleBrandingWhite() {
  return "";
}
